   "source": [
    "## ====== 设置与依赖 ======\n",
    "# setwd(\"D:/R work\")  # 如需\n",
    "library(readxl)\n",
    "library(tidyverse)   # dplyr/ggplot2/tidyr/forcats\n",
    "library(Boruta)\n",
    "library(ranger)\n",
    "library(ggridges)\n",
    "library(scales)\n",
    "\n",
    "set.seed(250307)\n",
    "\n",
    "## ====== 读取数据并准备 ======\n",
    "dat <- read_excel(\"Raw_data_MLET.xlsx\")\n",
    "\n",
    "# 排除不参与建模的列\n",
    "drop_cols <- c(\"exercise_tolerance\",\"VO2peak\",\"AT\",\"ATHR\",\"HRmax\",\"VEVCO2Slope\")\n",
    "X <- dat %>% select(-all_of(drop_cols))\n",
    "\n",
    "# 目标变量\n",
    "y <- factor(dat$exercise_tolerance)\n",
    "\n",
    "# 处理：字符转因子；数值缺失用中位数填补（ranger 可混合数值/因子）\n",
    "X <- X %>%\n",
    "  mutate(across(where(is.character), as.factor))\n",
    "for (v in names(X)) {\n",
    "  if (is.numeric(X[[v]]) && anyNA(X[[v]])) {\n",
    "    X[[v]][is.na(X[[v]])] <- median(X[[v]], na.rm = TRUE)\n",
    "  }\n",
    "}\n",
    "\n",
    "df_boruta <- bind_cols(X, outcome = y)\n",
    "\n",
    "## ====== Boruta 特征选择（保留历史以画分布） ======\n",
    "bor <- Boruta(\n",
    "  outcome ~ .,\n",
    "  data       = df_boruta,\n",
    "  pValue     = 0.01,\n",
    "  mcAdj      = TRUE,\n",
    "  maxRuns    = 200,\n",
    "  doTrace    = 2,\n",
    "  holdHistory = TRUE,\n",
    "  getImp     = getImpRfZ\n",
    ")\n",
    "\n",
    "# 打印最终确认变量\n",
    "confirmed <- names(bor$finalDecision)[bor$finalDecision == \"Confirmed\"]\n",
    "cat(\"Boruta confirmed features:\\n\"); print(confirmed)\n",
    "\n",
    "## ====== 组装“重要性历史”用于山脊图 ======\n",
    "imp_history <- as.data.frame(bor$ImpHistory)   # 每轮重要性Z分数\n",
    "shadow_cols <- grep(\"shadow\", names(imp_history), value = TRUE, ignore.case = TRUE)\n",
    "real_cols   <- setdiff(names(imp_history), shadow_cols)\n",
    "\n",
    "# 真实特征的最终决策\n",
    "final_decision <- tibble(\n",
    "  feature  = real_cols,\n",
    "  decision = bor$finalDecision[real_cols] |> as.character()\n",
    ")\n",
    "\n",
    "# 影子特征（仅用于参考，可在图例里区分；如不需要可注释掉）\n",
    "shadow_decision <- tibble(\n",
    "  feature  = shadow_cols,\n",
    "  decision = rep(\"Shadow\", length(shadow_cols))\n",
    ")\n",
    "\n",
    "decision_all <- bind_rows(final_decision, shadow_decision)\n",
    "\n",
    "imp_long <- imp_history %>%\n",
    "  pivot_longer(cols = everything(), names_to = \"feature\", values_to = \"importance\") %>%\n",
    "  filter(is.finite(importance)) %>%\n",
    "  left_join(decision_all, by = \"feature\")\n",
    "\n",
    "# y 轴顺序：按真实特征的中位重要性从小到大（影子放最下方）\n",
    "order_tbl <- imp_long %>%\n",
    "  filter(feature %in% real_cols) %>%\n",
    "  group_by(feature) %>% summarise(med = median(importance, na.rm = TRUE)) %>%\n",
    "  arrange(med)\n",
    "\n",
    "imp_long <- imp_long %>%\n",
    "  mutate(feature = fct_relevel(feature, c(order_tbl$feature, setdiff(unique(feature), order_tbl$feature))))\n",
    "\n",
    "## ====== 山脊图（无标题）并保存高清 ======\n",
    "p_ridge <- ggplot(imp_long %>% filter(feature %in% real_cols),  # 只画真实特征\n",
    "                  aes(x = importance, y = feature, fill = decision)) +\n",
    "  geom_density_ridges_gradient(scale = 3, rel_min_height = 0.01, color = \"black\", size = 0.2) +\n",
    "  scale_fill_manual(values = c(\"Confirmed\" = alpha(\"#02BBC1\",0.7),\n",
    "                               \"Tentative\" = alpha(\"#FFC107\",0.7),\n",
    "                               \"Rejected\"  = alpha(\"#E53935\",0.7),\n",
    "                               \"Shadow\"    = alpha(\"#757575\",0.6))) +\n",
    "  labs(x = \"Importance (Z-score)\", y = \"Features\") +  # 无标题\n",
    "  theme_bw(base_size = 12) +\n",
    "  theme(legend.position = \"right\",\n",
    "        panel.spacing   = unit(0.1, \"lines\"),\n",
    "        axis.text.y     = element_text(size = 9))\n",
    "\n",
    "print(p_ridge)\n",
    "ggsave(\"Boruta_RidgePlot.png\", p_ridge, width = 8, height = 10, dpi = 600)\n",
    "ggsave(\"Boruta_RidgePlot.pdf\", p_ridge, width = 8, height = 10)\n",
    "\n",
    "# （如需包含 Shadow 分布，把上面过滤里的 %>% filter(feature %in% real_cols) 去掉）\n"
   ]
