    "# ==== Packages ====\n",
    "library(readxl)\n",
    "library(glmnet)\n",
    "\n",
    "# ==== Data ====\n",
    "df <- read_excel(\"Raw_data_MLET.xlsx\")\n",
    "drop_cols <- c(\"exercise_tolerance\",\"VO2peak\",\"AT\",\"ATHR\",\"HRmax\",\"VEVCO2Slope\")\n",
    "y <- as.factor(df$exercise_tolerance)\n",
    "X <- df[, setdiff(names(df), drop_cols), drop = FALSE]\n",
    "\n",
    "# 简单缺失处理：数值-中位数；非数值-众数并转因子\n",
    "for (nm in names(X)) {\n",
    "  if (is.numeric(X[[nm]])) {\n",
    "    if (anyNA(X[[nm]])) X[[nm]][is.na(X[[nm]])] <- median(X[[nm]], na.rm = TRUE)\n",
    "  } else {\n",
    "    X[[nm]] <- as.factor(X[[nm]])\n",
    "    if (anyNA(X[[nm]])) {\n",
    "      tb <- table(X[[nm]]); mode_val <- names(which.max(tb))\n",
    "      X[[nm]][is.na(X[[nm]])] <- mode_val\n",
    "    }\n",
    "  }\n",
    "}\n",
    "# 模型矩阵（哑变量；无截距）\n",
    "Xmm <- model.matrix(~ . - 1, data = X)\n",
    "\n",
    "# ==== LASSO with 10-fold CV ====\n",
    "set.seed(123)\n",
    "cvfit <- cv.glmnet(Xmm, y, family = \"binomial\", alpha = 1, nfolds = 10)\n",
    "fit <- cvfit$glmnet.fit\n",
    "lam_min <- cvfit$lambda.min\n",
    "lam_1se <- cvfit$lambda.1se\n",
    "\n",
    "# ==== 保存：系数路径图（无标题） ====\n",
    "png(\"LASSO_Coefficient_Path.png\", width = 2000, height = 1600, res = 300)\n",
    "plot(fit, xvar = \"lambda\", main = \"\")\n",
    "abline(v = log(lam_min), lty = 2)\n",
    "abline(v = log(lam_1se), lty = 2)\n",
    "dev.off()\n",
    "\n",
    "pdf(\"LASSO_Coefficient_Path.pdf\", width = 8, height = 6)\n",
    "plot(fit, xvar = \"lambda\", main = \"\")\n",
    "abline(v = log(lam_min), lty = 2)\n",
    "abline(v = log(lam_1se), lty = 2)\n",
    "dev.off()\n",
    "\n",
    "# ==== 保存：CV 曲线（无标题） ====\n",
    "png(\"LASSO_CV_Curve.png\", width = 2000, height = 1600, res = 300)\n",
    "plot(cvfit, main = \"\")\n",
    "abline(v = log(lam_min), lty = 3)\n",
    "abline(v = log(lam_1se), lty = 3)\n",
    "dev.off()\n",
    "\n",
    "pdf(\"LASSO_CV_Curve.pdf\", width = 8, height = 6)\n",
    "plot(cvfit, main = \"\")\n",
    "abline(v = log(lam_min), lty = 3)\n",
    "abline(v = log(lam_1se), lty = 3)\n",
    "dev.off()\n",
    "\n",
    "# λ.min\n",
    "co_min <- coef(cvfit, s = \"lambda.min\")\n",
    "nm_min <- rownames(co_min)\n",
    "b_min  <- as.numeric(co_min)\n",
    "idx_min <- which(b_min != 0 & nm_min != \"(Intercept)\")\n",
    "keep_min <- nm_min[idx_min]\n",
    "tbl_min <- data.frame(feature = keep_min, beta = b_min[idx_min])\n",
    "tbl_min <- tbl_min[order(-abs(tbl_min$beta)), ]\n",
    "\n",
    "cat(\"\\n[LASSO @ lambda.min]\\n\")\n",
    "cat(\"Retained variables:\", length(keep_min), \"\\n\")\n",
    "print(tbl_min, row.names = FALSE)\n",
    "write.csv(tbl_min, \"LASSO_features_lambda_min.csv\", row.names = FALSE)\n",
    "\n",
    "# λ.1se\n",
    "co_1se <- coef(cvfit, s = \"lambda.1se\")\n",
    "nm_1se <- rownames(co_1se)\n",
    "b_1se  <- as.numeric(co_1se)\n",
    "idx_1se <- which(b_1se != 0 & nm_1se != \"(Intercept)\")\n",
    "keep_1se <- nm_1se[idx_1se]\n",
    "tbl_1se <- data.frame(feature = keep_1se, beta = b_1se[idx_1se])\n",
    "tbl_1se <- tbl_1se[order(-abs(tbl_1se$beta)), ]\n",
    "\n",
    "cat(\"\\n[LASSO @ lambda.1se]\\n\")\n",
    "cat(\"Retained variables:\", length(keep_1se), \"\\n\")\n",
    "print(tbl_1se, row.names = FALSE)\n",
    "write.csv(tbl_1se, \"LASSO_features_lambda_1se.csv\", row.names = FALSE)"
   ]
