   "source": [
    "# ==== 提取子集变量 ====\n",
    "vars_to_keep <- c(\"Age\", \"BMI\", \"Diabetes\", \"Gender\", \"Hb\", \"RBC\", \"RHR\", \"Smoking\")\n",
    "sub_df <- X[, vars_to_keep]\n",
    "\n",
    "# ==== One-hot 编码并去除截距 ====\n",
    "Xmm_sub <- model.matrix(~ . - 1, data = sub_df)\n",
    "Xmm_sub <- as.data.frame(Xmm_sub)\n",
    "\n",
    "# ==== 计算 VIF ====\n",
    "library(car)\n",
    "set.seed(123)\n",
    "dummy_y <- rnorm(nrow(Xmm_sub))\n",
    "lm_model <- lm(dummy_y ~ ., data = Xmm_sub)\n",
    "vif_values <- vif(lm_model)\n",
    "\n",
    "# 排序输出\n",
    "vif_table <- sort(vif_values, decreasing = TRUE)\n",
    "print(round(vif_table, 3))"
   ]
